const dependencies = {
  get connect () {
    const singleton = require('./connect/singleton')
    Object.defineProperty(dependencies, 'connect', { value: singleton })
    return singleton
  },
  get statements () {
    const statements = require('./statements/index')
    Object.defineProperty(dependencies, 'statements', { value: statements })
    return statements
  },
  get Service () {
    const Service = require('./connect/Service')
    Object.defineProperty(dependencies, 'Service', { value: Service })
    return Service
  },
  inject: (...args) => {
    require('./cds').inject(...args)
    return this
  }
}
module.exports = dependencies
