const dependencies = {
  get Client () {
    const Client = require('./client/Client')
    Object.defineProperty(dependencies, 'Client', { value: Client })
    return Client
  },
  get options () {
    const options = require('./client/options')
    Object.defineProperty(dependencies, 'options', { value: options })
    return options
  },
  get httpFunctions () {
    const httpFunctions = require('./client/httpFunctions')
    Object.defineProperty(dependencies, 'httpFunctions', { value: httpFunctions })
    return httpFunctions
  },
  get serviceFunctions () {
    const serviceFunctions = require('./client/serviceFunctions')
    Object.defineProperty(dependencies, 'serviceFunctions', { value: serviceFunctions })
    return serviceFunctions
  },
  inject: (...args) => {
    return require('./cds').inject(...args)
  }
}

module.exports = dependencies
