import { default as core } from "./reflect"
declare global { const cds : facade }
export = cds

type facade = core
& import ("./models")
& import ("./connect")
& import ("./serve")
& import ("./ql")._cds

declare global {
	class SELECT<T> extends cds.ql.SELECT<T>{}
	class INSERT<T> extends cds.ql.INSERT<T>{}
	class UPDATE<T> extends cds.ql.UPDATE<T>{}
	class DELETE<T> extends cds.ql.DELETE<T>{}
	class CREATE extends cds.ql.CREATE{}
	class DROP extends cds.ql.DROP{}
}
