module.exports = Object.assign(disconnect, {
    options: ['--all'],
    shortcuts: ['-a'],
    help: `
# SYNOPSIS

    *cds disconnect* [ <extension project directory> ]

   Deletes extension project settings


# OPTIONS

    *-a* | *--all*
    
        Delete all project settings
        
`})


async function disconnect ([directory], options = {}) {

    try {
        const client = require('@sap/cds-sidecar-client')

        client.disconnect(directory, options.all)
    } catch (e) {
        console.log('This feature requires cds-sidecar-client to be installed')
    }

}



