const util = require('util')
util.inspect.defaultOptions.colors = true
util.inspect.defaultOptions.depth = 11

function stdout (...args) {
  process.stdout.write('[cql] - ' + util.format(...args) + '\n')
}

function stderr (...args) {
  process.stderr.write('[cql] - ' + util.format(...args) + '\n')
}

module.exports = {
  stderr,
  stdout
}
