const fs = require('@sap/cds-foss')('fs-extra')
const path = require('path')
const BuildTaskHandlerOData = require('../buildTaskHandlerOData')
const { BUILD_OPTION_OUTPUT_MODE, OUTPUT_MODE_RESULT_ONLY, FILE_EXT_CDS } = require('../constants')
const DEBUG = process.env.DEBUG

const DEFAULT_COMPILER_DEST_FOLDER = path.join("src", "main", "resources", "edmx")

class JavaCfModuleBuilder extends BuildTaskHandlerOData {
    constructor(task, buildOptions) {
        super("Java CF Module Builder", task, buildOptions)
    }

    init() {
        const { dest, options } = this.task
        options.compileDest = options.compileDest ? path.resolve(this.buildOptions.target, options.compileDest) : path.resolve(dest, DEFAULT_COMPILER_DEST_FOLDER)
    }

    async build() {
        const modelPaths = this._resolveModel()
        const { src, dest, options } = this.task

        this.logger.log(`\n[cds] - building module [${this._stripProjectPaths(src)}] using [${this.name}]`)

        if (!modelPaths || modelPaths.length === 0) {
            this.logger.log(`[cds] - no model found, skip build`)
            return this._result
        }

        this.logger.log(`[cds] - model: ${this._stripProjectPaths(modelPaths).join(", ")}`)

        const model = await this.cds.load(modelPaths)

        const promises = [
            this._compileCsn(model, options.compileDest),
            this._compileEdmx(model, options.compileDest)
        ]

        if (!this.hasBuildOption(BUILD_OPTION_OUTPUT_MODE, OUTPUT_MODE_RESULT_ONLY)) {
            promises.push(this._copyNativeContent(src, dest))
        }
        await Promise.all(promises)

        return this._result
    }

    async clean() {
        // staging build content has already been deleted
        if (this.env.build.target === ".") {
            if (DEBUG) {
                this.logger.log(`Deleting build target folder ${this.task.options.compileDest}`)
            }
            await fs.remove(this.task.options.compileDest)
        }
    }

    async _copyNativeContent(src, dest) {
        return super._copyNativeContent(src, dest, (entry) => {
            if (fs.statSync(entry).isDirectory()) {
                return true // using common filter for folders
            } else {
                const extname = path.extname(entry)
                return extname !== FILE_EXT_CDS
            }
        })
    }
}
module.exports = JavaCfModuleBuilder
